  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all therapies as @therapies" do
      therapy = Therapy.create! valid_attributes
      get :index
      assigns(:therapies).should eq([therapy])
    end
  end

  describe "GET show" do
    it "assigns the requested therapy as @therapy" do
      therapy = Therapy.create! valid_attributes
      get :show, :id => therapy.id.to_s
      assigns(:therapy).should eq(therapy)
