  end

  describe "GET new" do
    it "assigns a new therapy as @therapy" do
      get :new
      assigns(:therapy).should be_a_new(Therapy)
    end
  end

  describe "GET edit" do
    it "assigns the requested therapy as @therapy" do
      therapy = Therapy.create! valid_attributes
      get :edit, :id => therapy.id.to_s
      assigns(:therapy).should eq(therapy)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Therapy" do
        expect {
          post :create, :therapy => valid_attributes
        }.to change(Therapy, :count).by(1)
      end

      it "assigns a newly created therapy as @therapy" do
        post :create, :therapy => valid_attributes
        assigns(:therapy).should be_a(Therapy)
