        assigns(:therapy).should be_a_new(Therapy)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Therapy.any_instance.stub(:save).and_return(false)
        post :create, :therapy => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested therapy" do
        therapy = Therapy.create! valid_attributes
        # Assuming there are no other therapies in the database, this
